import Link from 'next/link';
import fetch from 'isomorphic-unfetch';

import Layout from '../components/Layout';

const Index = props => (
  <Layout>
    <h1>Batman TV Show</h1>
    {props.shows.map((show, index) => (
      <li key={show.id}>
        <Link href="/p/[id]" as={`/p/${show.id}`}>
          <a>{show.name}</a>
        </Link>
      </li>
    ))}
  </Layout>
);

